var status = "negative";
node.status({text:"Processing number"}); 
if (msg.payload >= 0){ 
    setTimeout(function(){
        node.status({fill:"yellow",shape:"dot",text:"Processing"}); 
        setTimeout(function(){ 
            node.send({'Positive' : msg.payload});
            node.status({fill:"green",shape:"dot",text:"Done!"}); 
        },1000); 
    },1000);
    status = "positive";
}else{
    setTimeout(function(){
        node.status({fill:"yellow",shape:"dot",text:"Warning"});
        node.warn("Whoopsie ^_^.."); 
        setTimeout(function(){
            node.send({'Negative' : msg.payload});
            node.status({fill:"red",shape:"dot",text:"Error!"});
            node.error("The number is negative.", {'payload' : 'Error'}); 
        },1000);
    },1000);
    status = "negative";
}
msg.pos = status;
return msg;
